#include <stdint.h>
#include <stdio.h>
void waitForUserInput(void);

// assembly komultari:
// MRS : Move from special register to register
// MSR : Move from register to special register
// MOV : sadece general purpose registerlar icin kullanilir

// Syntax --> MRS register, special register    ( r <-- spc_r)

/* special register
 APSR, IPSR,EPSR;IEPSR,IAPSR,EAPSR,PSR,MSP,PSP,PRIMASK,BASEPRI,BASEPRI_MAX,FAULTMASK,CONTROL
 */

int main(void)
{

	int control_reg;
	// read Control register ( special reg) to control_reg (gp reg)
	asm volatile("MRS %0,CONTROL": "=r"(control_reg));

	waitForUserInput();
}




void waitForUserInput(void){
	while(getchar()!='\n'){

	}
	getchar();
}
